using System.Linq;
using Microsoft.VisualStudio.TestTools.UnitTesting;

namespace DNTCommon.Web.Core.Tests;

[TestClass]
public class HtmlHelperServiceTests : TestsBase
{
    public const string Html = @"
        <html>
        <head>
           <title>.NET Core Applications &#8211; DNT</title>
        </head>
        <body>
           <img width='300' id='X1' src='http://mysite.com/path/img1.png' />
           <a id='X2' HREF='http://mysite.com/path/img2.png'>image 2</a>
           <img id='X3' SRC='http://mysite.com/path/img2.png' >
           <img id='X4' src='http://mysite.com/path/img3.png?w=100&h=200' />
           <a id='X5' href='http://mysite.com/path/img1.png'>image 1</a>

           <img id='X6' src='/path/img1.png' />
           <img id='X7' src='file://path/img1.png' />
           <a id='X8' HREF='path/img2.png'>image 2</a>
           <img id='X9' SRC='./path/img2.png' >
           <img id='X10' src='../img3.png?w=100&h=200' />
           <a id='X11' href='www.site.com'>image 1</a>
           <img id='X12' width='300' src='data:image/svg+xml;base64,' />
        </body>
        </html>
        ";

    [TestMethod]
    public void TestExtractImagesLinksReturnsAllImages()
        => ServiceProvider.RunScopedService<IHtmlHelperService>(htmlHelperService =>
        {
            var imagesList = htmlHelperService.ExtractImagesLinks(Html).ToList();
            Assert.AreEqual(expected: 8, imagesList.Count);
        });

    [TestMethod]
    public void TestExtractLinksReturnsAllLinks()
        => ServiceProvider.RunScopedService<IHtmlHelperService>(htmlHelperService =>
        {
            var linksList = htmlHelperService.ExtractLinks(Html).ToList();
            Assert.AreEqual(expected: 4, linksList.Count);
        });

    [TestMethod]
    public void TestGetHtmlPageTitleWorks()
        => ServiceProvider.RunScopedService<IHtmlHelperService>(htmlHelperService =>
        {
            var title = htmlHelperService.GetHtmlPageTitle(Html);
            Assert.AreEqual(expected: ".NET Core Applications â€“ DNT", title);
        });

    [TestMethod]
    public void TestFixRelativeImagesOrUrlsWorks()
        => ServiceProvider.RunScopedService<IHtmlHelperService>(htmlHelperService =>
        {
            var html = htmlHelperService.FixRelativeUrls(Html, imageNotFoundPath: "content/path/image.png",
                siteBaseUrl: "http://www.mysite.com");

            Assert.AreEqual(
                expected:
                "\n        <html>\n        <head>\n           <title>.NET Core Applications &#8211; DNT</title>\n        </head>\n        <body>\n           <img width='300' id='X1' src='http://mysite.com/path/img1.png'>\n           <a id='X2' href='http://mysite.com/path/img2.png'>image 2</a>\n           <img id='X3' src='http://mysite.com/path/img2.png'>\n           <img id='X4' src='http://mysite.com/path/img3.png?w=100&h=200'>\n           <a id='X5' href='http://mysite.com/path/img1.png'>image 1</a>\n\n           <img id='X6' src='http://www.mysite.com/path/img1.png'>\n           <img id='X7' src='http://www.mysite.com/content/path/image.png'>\n           <a id='X8' href='http://www.mysite.com/path/img2.png'>image 2</a>\n           <img id='X9' src='http://www.mysite.com/path/img2.png'>\n           <img id='X10' src='http://www.mysite.com/img3.png?w=100&h=200'>\n           <a id='X11' href='http://www.site.com'>image 1</a>\n           <img id='X12' width='300' src='data:image/svg+xml;base64,'>\n        </body>\n        </html>\n        ",
                html);
        });
}